mapper.select:
select items.id , items.item_name , items.item_image , items.item_price , items.item_description , inventory.inventory , category.category_name from items join inventory on items.id = inventory.item_id join category on items.item_category = category.id where items.is_deleted = 0
and items.item_name like '%${name}%'
and items.item_category = #{categoryId}

mapper.update:
 	update
		inventory
	set
	inventory = #{itemInventory}
    where
    	item_id = #{id}
