mapper.insert:
insert into users(
    user_id
  , pass
  , family_name
  , first_name
  , birthday
  , gender
  , phone_number
)
values (
    #{userId}
  , #{password}
  , #{familyName}
  , #{firstName}
  , #{birthday}
  , #{gender}
  , #{phoneNumber}
)

   insert into address(
   user_id
      , postal_coder
      , prefectures
      , municipalities
      , number
    )
    values (
    #{userId}
	  , #{postalCoder}
      , #{prefectures}
      , #{municipalities}
      , #{number}
    )
